import  cv2
from urllib.request import urlopen
import numpy as np
import matplotlib.pyplot as plt

resp = urlopen('data:image/jpeg;base64,')
image = np.asarray(bytearray(resp.read()),dtype='uint8')
image = cv2.imdecode(image,cv2.IMREAD_COLOR)
image = cv2.cvtColor(image,cv2.COLOR_BGR2RGB)
image = image.reshape((image.shape[0]*image.shape[1],3))

R = image[:,0]
G = image[:,1]
B = image[:,2]

from matplotlib import colors
norm = colors.Normalize(vmin = -1.,vmax = 1.)
norm.autoscale(image)
pixel_colors = norm(image).tolist()

fig = plt.figure(figsize = (6,6))
ax = fig.add_subplot(111,projection = '3d')
ax.scatter(R,G,B,marker = '.', facecolors = pixel_colors)
ax.set_xlabel('Red')
ax.set_ylabel('Green')
ax.set_ylabel('Blue')
plt.show()



from sklearn.cluster import KMeans
k = 5
km = KMeans(n_clusters = k)
km.fit(image)

print(km.cluster_centers_)

fig = plt.figure(figsize=(6,6))
ax2 = fig.add_subplot(111, projection = '3d')
R2 = km.cluster_centers_[:,0]
G2 = km.cluster_centers_[:,1]
B2 = km.cluster_centers_[:,1]

from matplotlib import colors
norm2 = colors.Normalize(vmin=-1.,vmax=1.)
norm2.autoscale(image)
pixel_colors2 = norm2(km.cluster_centers_).tolist()

ax2.scatter(R2,G2,B2,marker = 'o',s = 15, facecolors = pixel_colors2)
ax2.set_xlabel('Red')
ax2.set_ylabel('Green')
ax2.set_zlabel('Blue')
plt.show()


































